import React, { useEffect, useState } from "react";
import image from "../../assets/product1/product1.png";
import { Col, Container, Row } from "react-bootstrap";
const OrderProduct = () => {
    const [drinks, setDrinks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Xử lý API
    useEffect(() => {
        const fetchDrinks = async () => {
            try {
                const response = await fetch("https://666964302e964a6dfed4f085.mockapi.io/Drinks");
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                const data = await response.json();
                setDrinks(data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchDrinks();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Xin hãy load lại trang {error}</div>;
    }

    // Hàm để tính giá sau khi giảm giá và định dạng số tiền
    const formatCurrency = (amount) => {
        return new Intl.NumberFormat("vi-VN", { style: "currency", currency: "VND" }).format(amount).replace(/\B(?=(\d{3})+(?!\d))/g, ".");
    };

    return (
        <div className="orderproduct">
            <Container>
                <Row>
                    <Col>
                        <div className="orderproduct-images">
                            <img src={image} alt="" />
                            <p></p>
                        </div>
                    </Col>
                    <Col>
                        <div className="orderproduct-choose">
                            <h2>Shaman Tea</h2>
                            <h4>23.000 vnd</h4>
                            <p>
                                Lorem, ipsum dolor sit amet consectetur adipisicing elit. Error, maiores! Explicabo eaque fugit ullam aut natus repellendus, at, facilis pariatur inventore architecto
                                consequatur commodi dolorem voluptate quam incidunt veniam ea?
                            </p>
                            <div className="orderproduct-choose_quantity">
                                <i className="fa-solid fa-minus"></i>
                                <h5>8</h5>
                                <i className="fa-solid fa-plus"></i>
                            </div>
                            Button
                        </div>
                    </Col>
                </Row>
            </Container>
        </div>
    );
};

export default OrderProduct;
