import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { Container } from "react-bootstrap";
import React, { useEffect, useState } from "react";
import Slider from "react-slick";
import HeadLine from "../../../component/Headline/Headline";

const MilkTea = () => {
    const [drinks, setDrinks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    //xu li API
    useEffect(() => {
        const fetchDrinks = async () => {
            try {
                const response = await fetch("https://666964302e964a6dfed4f085.mockapi.io/Drinks");
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                const data = await response.json();
                setDrinks(data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchDrinks();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    //react-slick
    function SampleNextArrow(props) {
        const { className, style, onClick } = props;
        return <div className={className} style={{ ...style, display: "block", backgroundColor: "red", color: "white", borderRadius: "50%", padding: "10px" }} onClick={onClick} />;
    }

    function SamplePrevArrow(props) {
        const { className, style, onClick } = props;
        return <div className={className} style={{ ...style, display: "block", backgroundColor: "red", color: "white", borderRadius: "50%", padding: "10px" }} onClick={onClick} />;
    }
    var settings = {
        infinite: true,
        speed: 500,
        slidesToScroll: 2,
        slidesToShow: 6,
        nextArrow: <SampleNextArrow />,
        prevArrow: <SamplePrevArrow />,
    };

    return (
        <div className="milkTea">
            <HeadLine headline={"Milk Tea"} />
            <Container>
                <div className="slider-container">
                    <Slider {...settings}>
                        {drinks.map((drink) => (
                            <div className="milktea-box" key={drink.id}>
                                <div className="milkTea-box_image">
                                    <img src={drink.image} alt={drink.name} />
                                    <p>{drink.description}</p>
                                </div>
                                <div className="milkTea-box_title">
                                    <h2>{drink.nameDrink}</h2>
                                    <div className="milkTea-box_price">
                                        <h3 className={drink.priceSale !== 0 ? "sale" : ""}>{drink.price}VND</h3>
                                        {drink.priceSale !== 0 && <h4>{drink.priceSale} VND</h4>}
                                    </div>
                                </div>
                            </div>
                        ))}
                    </Slider>
                </div>
            </Container>
        </div>
    );
};

export default MilkTea;
