import React, { useEffect, useState } from "react";
import { Col, Container, Row } from "react-bootstrap";
import { useParams } from "react-router-dom";
import Button from "../../component/Button/Button";
import image from "../../assets/product1/product1.png";
import HeadLine from "../../component/Headline/Headline";
import Slider from "react-slick";

const OrderProduct = () => {
    const [quantity, setQuantity] = useState(1);
    const [drinks, setDrinks] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const { drinkId } = useParams();

    // Quantity handling
    const handleChooseQuantity = (type) => {
        if (type === "plus") {
            setQuantity(quantity + 1);
        } else if (type === "minus" && quantity > 1) {
            setQuantity(quantity - 1);
        }
    };

    // Fetch drinks data from API
    useEffect(() => {
        const fetchDrinks = async () => {
            try {
                const response = await fetch(`https://666964302e964a6dfed4f085.mockapi.io/Drinks/${drinkId}`);
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                const data = await response.json();
                setDrinks(data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchDrinks();
    }, [drinkId]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Xin hãy load lại trang</div>;
    }

    // Format currency function
    const formatCurrency = (amount) => {
        return new Intl.NumberFormat("vi-VN", { style: "currency", currency: "VND" }).format(amount).replace(/\B(?=(\d{3})+(?!\d))/g, ".");
    };

    // Calculate sale price if applicable
    const getSalePrice = () => {
        if (drinks.percentSale !== "0") {
            const salePrice = drinks.price - (drinks.price * drinks.percentSale) / 100;
            return (
                <>
                    <span style={{ textDecoration: "line-through", color: "black" }}>{formatCurrency(drinks.price)}</span>
                    <span style={{ color: "red", marginLeft: "10px" }}>{formatCurrency(salePrice)}</span>
                </>
            );
        } else {
            return <span>{formatCurrency(drinks.price)}</span>;
        }
    };

    return (
        <div className="orderproduct">
            <Container>
                <Row>
                    <Col md={12} lg={6}>
                        <div className="orderproduct-images">
                            <img src={image} alt="" />
                            {drinks.percentSale !== "0" && <p>{drinks.percentSale}%</p>}
                        </div>
                    </Col>
                    <Col md={12} lg={6}>
                        <div className="orderproduct-choose">
                            <h2>{drinks.nameDrink}</h2>
                            <div className="orderproduct-price">
                                <h4>{getSalePrice()}</h4>
                            </div>
                            <div className="orderproduct-choose_quantity">
                                <i className="fa-solid fa-minus" onClick={() => handleChooseQuantity("minus")}></i>
                                <h5>{quantity}</h5>
                                <i className="fa-solid fa-plus" onClick={() => handleChooseQuantity("plus")}></i>
                            </div>
                            <div className="orderproduct-choose_order">
                                <Button secondary text="Đặt hàng" link="" />
                                <Button text="Mua Ngay" link="" />
                            </div>
                        </div>
                    </Col>
                </Row>
            </Container>
        </div>
    );
};

export default OrderProduct;
