import React, { useEffect, useState } from "react";

const UseFetch = (linkId) => {
    const [drinks, setDrinks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchDrinks = async () => {
            setLoading(true);
            try {
                const response = await fetch("https://666964302e964a6dfed4f085.mockapi.io/Drinks/" + linkId);
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                const data = await response.json();
                setDrinks(data);
            } catch (error) {
                setError(error); // Lưu toàn bộ object lỗi để có thể sử dụng các thông tin bổ sung
            } finally {
                setLoading(false); // Kết thúc loading sau khi fetch hoàn thành (thành công hoặc thất bại)
            }
        };

        fetchDrinks();
    }, [linkId]); // Thêm linkId vào mảng dependency để fetch lại khi linkId thay đổi

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Xin hãy load lại trang: {error.message}</div>; // Hiển thị message của error
    }

    return drinks;
};

export default UseFetch;
