import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { Container } from "react-bootstrap";
import React, { useEffect, useState } from "react";
import Slider from "react-slick";
import HeadLine from "../../../component/Headline/Headline";

const MilkTea = () => {
    const [selectedType, setSelectedType] = useState(""); // State để lưu trữ loại sản phẩm được chọn
    const [drinks, setDrinks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Xử lý API
    useEffect(() => {
        const fetchDrinks = async () => {
            try {
                const response = await fetch("https://666964302e964a6dfed4f085.mockapi.io/Drinks");
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                const data = await response.json();
                setDrinks(data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchDrinks();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    // Hàm để tính giá sau khi giảm giá và định dạng số tiền
    const formatCurrency = (amount) => {
        return new Intl.NumberFormat("vi-VN", { style: "currency", currency: "VND" }).format(amount).replace(/\B(?=(\d{3})+(?!\d))/g, ".");
    };

    //react-slick
    const settings = {
        infinite: true,
        speed: 500,
        slidesToScroll: 2,
        slidesToShow: 6,
    };

    // Lọc và phân loại các mặt hàng dựa trên loại sản phẩm được chọn
    const filteredDrinks = selectedType ? drinks.filter((drink) => drink.type === selectedType) : drinks;

    return (
        <div className="milkTea">
            <Container>
                <div className="slider-container">
                    <HeadLine headline={"Milk Tea"} />
                    <div>
                        <button onClick={() => setSelectedType("Milk Tea")}>Milk Tea</button>
                        <button onClick={() => setSelectedType("Fresh Tea")}>Fresh Tea</button>
                        <button onClick={() => setSelectedType("None Tea")}>None Tea</button>
                        {/* Thêm các nút khác nếu cần */}
                        <button onClick={() => setSelectedType("")}>Show All</button>
                    </div>
                    <Slider {...settings}>
                        {filteredDrinks.map((drink) => (
                            <div className="milktea-box" key={drink.id}>
                                <div className="milkTea-box_image">
                                    <img src={drink.image} alt={drink.name} />
                                    {drink.percentSale !== "0" && <p>{drink.percentSale}%</p>}
                                </div>

                                <div className="milkTea-box_title">
                                    <h2>{drink.nameDrink}</h2>
                                    <div className="milkTea-box_price">
                                        <h3 className={drink.percentSale !== "0" ? "sale" : ""}>{formatCurrency(drink.price)}</h3>
                                        {drink.percentSale !== "0" && <h4>{formatCurrency(drink.price - (drink.price * drink.percentSale) / 100)}</h4>}
                                    </div>
                                </div>
                            </div>
                        ))}
                    </Slider>
                </div>
            </Container>
        </div>
    );
};

export default MilkTea;
