import React, { useState } from "react";
import axios from "axios";
import HeadLine from "../../component/Headline/Headline";
import { Container } from "react-bootstrap";

const Recruitment = () => {
    const [formData, setFormData] = useState({
        fullName: "",
        dob: "",
        address: "",
        position: "",
        phone: "",
    });

    const [errors, setErrors] = useState({});

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const validate = () => {
        let errors = {};
        if (!formData.fullName) errors.fullName = "Họ và tên là bắt buộc";
        if (!formData.dob) errors.dob = "Ngày tháng năm sinh là bắt buộc";
        else {
            // Calculate age from date of birth
            const today = new Date();
            const birthDate = new Date(formData.dob);
            const age = today.getFullYear() - birthDate.getFullYear();
            const monthDiff = today.getMonth() - birthDate.getMonth();

            if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
                age--;
            }

            // Check if age is less than 18
            if (age < 18) {
                errors.dob = "Bạn phải đủ 18 tuổi trở lên để đăng kí";
            }
        }
        if (!formData.address) errors.address = "Địa chỉ thường trú là bắt buộc";
        if (!formData.position) errors.position = "Vị trí ứng tuyển là bắt buộc";
        if (!formData.phone) {
            errors.phone = "Số điện thoại là bắt buộc";
        } else if (!/^\d{10}$/.test(formData.phone)) {
            errors.phone = "Số điện thoại phải có 10 chữ số";
        }
        return errors;
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const validationErrors = validate();
        if (Object.keys(validationErrors).length > 0) {
            setErrors(validationErrors);
        } else {
            axios
                .post("https://666fee480900b5f87248b557.mockapi.io/drinks", {
                    ...formData,
                    id: null,
                })
                .then((response) => {
                    alert("Đăng kí thành công");
                    setFormData({
                        fullName: "",
                        dob: "",
                        address: "",
                        position: "",
                        phone: "",
                    });
                    setErrors({});
                    console.log("aa");
                })
                .catch((error) => {
                    alert("Đăng kí không thành công. Vui lòng thử lại sau.");
                    console.log("hihi");
                });
        }
    };

    const formik = useFormik({
        initialValues: {
            username: "",
            email: "",
            password: "",
            repassword: "",
        },
        validationSchema: Yup.object({
            username: Yup.string().max(15, "Must be 15 characters or less").required("Required"),
            email: Yup.string().email("Invalid email address").required("Required"),
            password: Yup.string()
                .required("Password is required")
                .matches(
                    /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{8,}$/,
                    "Password must contain at least one uppercase letter, one lowercase letter, one special character, and one number, and be at least 8 characters long."
                ),
            repassword: Yup.string()
                .oneOf([Yup.ref("password"), null], "Passwords must match")
                .required("Confirm password is required"),
        }),
        onSubmit: async (values) => {
            console.log(showUser.email);
            const checkEmail = showUser.find((item) => item.email === values.email);

            if (checkEmail) {
                alert("email exists");
            } else {
                const hashedPassword = await bcrypt.hash(values.password, 10);
                const formData = {
                    username: values.username,
                    email: values.email,
                    password: hashedPassword,
                    permission: "0",
                };

                postData({
                    username: values.username,
                    email: values.email,
                });
                try {
                    const res = await axios.post("https://663466b09bb0df2359a1706c.mockapi.io/user", formData);
                    if (res.status === 201) {
                        alert("Register Success");
                    }
                } catch (error) {
                    console.error("Error posting data:", error.message);
                }
            }
        },
    });

    return (
        <div className="recruitment">
            <HeadLine headline={"TUYỂN DỤNG"}></HeadLine>
            <div className="recruitment-title">
                <Container>
                    <h3>GÓC TUYỂN PHA CHẾ</h3>
                    <div className="recruitment-address">
                        <i className="fa-solid fa-store"></i>
                        <a href="https://maps.app.goo.gl/9bxnN5UUarjkzHkc6" target="_blank">
                            CN1 - 79 Dân Chủ, Phường Bình Thọ, Quận 9, Thành Phố Hồ Chí Minh
                        </a>
                    </div>
                </Container>
            </div>
            <h2> Thông tin ứng tuyển</h2>
            <form onSubmit={formik.handleSubmit} className="recruitment-form">
                <div className="recruitment-form_input">
                    <label>Họ và tên:</label>
                    <input type="text" name="fullName" value={formData.fullName} onChange={handleChange} />
                    {formik.touched.fullName && formik.errors.fullName ? (
                        <div span className="error">
                            {formik.errors.fullName}
                        </div>
                    ) : null}
                </div>
                <div className="recruitment-form_input">
                    <label>Ngày tháng năm sinh:</label>
                    <input type="date" name="dob" value={formData.dob} onChange={handleChange} />
                    {errors.dob && <span className="error">{errors.dob}</span>}
                    {formik.touched.dob && formik.errors.dob ? (
                        <div span className="error">
                            {formik.errors.dob}
                        </div>
                    ) : null}
                </div>
                <div className="recruitment-form_input">
                    <label>Địa chỉ thường trú:</label>
                    <input type="text" name="address" value={formData.address} onChange={handleChange} />
                    {formik.touched.address && formik.errors.address ? (
                        <div span className="error">
                            {formik.errors.address}
                        </div>
                    ) : null}
                </div>
                <div className="recruitment-form_input">
                    <label>Vị trí ứng tuyển ( Fulltime / Parttime ):</label>
                    <input type="text" name="position" value={formData.position} onChange={handleChange} />
                    {errors.position && <span className="error">{errors.position}</span>}
                </div>
                <div className="recruitment-form_input">
                    <label>Số điện thoại:</label>
                    <input type="text" name="phone" value={formData.phone} onChange={handleChange} />
                    {errors.phone && <span className="error">{errors.phone}</span>}
                </div>
                <button type="submit">Đăng kí</button>
            </form>
        </div>
    );
};

export default Recruitment;
