import React, { useEffect, useState } from "react";
import { Container, Col, Row } from "react-bootstrap";
import { useParams, useNavigate } from "react-router-dom";
import Button from "../../component/Button/Button";
import HeadLine from "../../component/Headline/Headline";
import Slider from "react-slick";

const OrderProduct = () => {
    const { drinkId } = useParams();
    const [drinkInfo, setDrinkInfo] = useState(null);
    const [toppingDrinks, setToppingDrinks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();
    const [quantity, setQuantity] = useState(1);

    // Fetch drink info and topping drinks data from APIs
    useEffect(() => {
        const fetchDrinkInfo = async () => {
            try {
                const response = await fetch(`https://666964302e964a6dfed4f085.mockapi.io/Drinks/${drinkId}`);
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                const data = await response.json();
                setDrinkInfo(data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        const fetchToppingDrinks = async () => {
            try {
                const response = await fetch(`https://666964302e964a6dfed4f085.mockapi.io/Drinks?type=Topping`);
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                const data = await response.json();
                setToppingDrinks(data);
            } catch (error) {
                console.error("Error fetching topping drinks:", error);
            }
        };

        fetchDrinkInfo();
        fetchToppingDrinks();
    }, [drinkId]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error || !drinkInfo) {
        return <div>Xin hãy load lại trang</div>;
    }

    // Format currency function
    const formatCurrency = (amount) => {
        return new Intl.NumberFormat("vi-VN", { style: "currency", currency: "VND" }).format(amount).replace(/\B(?=(\d{3})+(?!\d))/g, ".");
    };

    // Handle order button click
    const handleOrderClick = () => {
        navigate(`/orderproduct/${drinkId}`);
    };

    // react-slick settings
    const sliderSettings = {
        dots: false,
        infinite: false,
        speed: 500,
        slidesToShow: 5,
        slidesToScroll: 5,
        initialSlide: 0,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 4,
                    slidesToScroll: 4,
                    infinite: true,
                    dots: true,
                },
            },
            {
                breakpoint: 767,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    initialSlide: 3,
                },
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2,
                },
            },
        ],
    };

    return (
        <div className="orderproduct">
            <Container>
                <div className="orderproduct-back" onClick={() => navigate("/")}>
                    <i className="fa-solid fa-chevron-left"></i>
                    <p>Trang chủ</p>
                </div>
                <Row>
                    <Col md={12} lg={6}>
                        <div className="orderproduct-images">
                            <img src={drinkInfo.image} alt="" />
                            {drinkInfo.percentSale !== "0" && <p>{drinkInfo.percentSale}%</p>}
                        </div>
                    </Col>
                    <Col md={12} lg={6}>
                        <div className="orderproduct-choose">
                            <h2>{drinkInfo.nameDrink}</h2>
                            <div className="orderproduct-price">
                                <h4>
                                    {drinkInfo.percentSale !== "0" && (
                                        <>
                                            <span className="priceLineThrough">{formatCurrency(drinkInfo.price)}</span>
                                            <span className="priceSale">{formatCurrency(drinkInfo.price - (drinkInfo.price * drinkInfo.percentSale) / 100)}</span>
                                        </>
                                    )}
                                    {drinkInfo.percentSale === "0" && <span className="priceNoSale">{formatCurrency(drinkInfo.price)}</span>}
                                </h4>
                                <p>{drinkInfo.description}</p>
                            </div>
                            <div className="orderproduct-choose_quantity">
                                <i className="fa-solid fa-minus" onClick={() => setQuantity((prevQuantity) => Math.max(prevQuantity - 1, 1))}></i>
                                <h5>{quantity}</h5>
                                <i className="fa-solid fa-plus" onClick={() => setQuantity((prevQuantity) => prevQuantity + 1)}></i>
                            </div>
                            <div className="orderproduct-choose_order">
                                <Button secondary text="Đặt hàng" onClick={handleOrderClick} />
                                <Button text="Mua Ngay" />
                            </div>
                        </div>
                    </Col>
                </Row>
                <div className="milkTea-container">
                    <div className="milkTea-parent">
                        <HeadLine headline="Topping" />
                        <Slider {...sliderSettings}>
                            {toppingDrinks.map((toppingDrink) => (
                                <div className="milktea-box" key={toppingDrink.id}>
                                    <div className="milkTea-box_image">
                                        <img src={toppingDrink.image} alt={toppingDrink.name} />
                                        {toppingDrink.percentSale !== "0" && <p>{toppingDrink.percentSale}%</p>}
                                    </div>
                                    <div className="milkTea-box_title">
                                        <h2>{toppingDrink.nameDrink}</h2>
                                    </div>
                                    <Button text="Đặt hàng" />
                                </div>
                            ))}
                        </Slider>
                    </div>
                </div>
            </Container>
        </div>
    );
};

export default OrderProduct;
