import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./ListMenu.css"; // Import the CSS file

const ListMenu = () => {
    const [drinks, setDrinks] = useState([]);
    const [formData, setFormData] = useState({
        id: "",
        nameDrink: "",
        description: "",
        type: "",
        image: "",
        price: "",
        percentSale: "",
    });
    const navigate = useNavigate();

    useEffect(() => {
        const user = localStorage.getItem("USERLOGIN");
        if (!user) {
            navigate("/admin");
        } else {
            fetchDrinks();
        }
    }, [navigate]);

    const fetchDrinks = async () => {
        try {
            const response = await fetch("https://666fee480900b5f87248b557.mockapi.io/drinks");
            const data = await response.json();
            setDrinks(data);
        } catch (error) {
            console.error("Error fetching drinks:", error);
        }
    };

    const handleDelete = async (id) => {
        try {
            await fetch(`https://666fee480900b5f87248b557.mockapi.io/drinks/${id}`, {
                method: "DELETE",
            });
            setDrinks((prevDrinks) => prevDrinks.filter((drink) => drink.id !== id));
        } catch (error) {
            console.error("Lỗi khi xoá:", error);
        }
    };

    const handleEdit = (id) => {
        const drink = drinks.find((d) => d.id === id);
        if (drink) {
            setFormData(drink);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    return (
        <div className="list-menu">
            <h2>List of Drinks</h2>
            <div className="input-form">
                <input type="text" name="id" value={formData.id} placeholder="ID" onChange={handleChange} readOnly />
                <input type="text" name="nameDrink" value={formData.nameDrink} placeholder="Tên Món" onChange={handleChange} />
                <input type="text" name="description" value={formData.description} placeholder="Mô Tả Món" onChange={handleChange} />
                <input type="text" name="type" value={formData.type} placeholder="Loại" onChange={handleChange} />
                <input type="text" name="image" value={formData.image} placeholder="Hình ảnh" onChange={handleChange} />
                <input type="text" name="price" value={formData.price} placeholder="Giá tiền" onChange={handleChange} />
                <input type="text" name="percentSale" value={formData.percentSale} placeholder="Phần trăm Sale" onChange={handleChange} />
            </div>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Tên Món</th>
                        <th>Mô Tả Món</th>
                        <th>Loại</th>
                        <th>Hình ảnh</th>
                        <th>Giá tiền</th>
                        <th>Phần trăm Sale</th>
                        <th>Sửa</th>
                        <th>Xoá</th>
                    </tr>
                </thead>
                <tbody>
                    {drinks.map((drink) => (
                        <tr key={drink.id}>
                            <td>{drink.id}</td>
                            <td>{drink.nameDrink}</td>
                            <td>{drink.description}</td>
                            <td>{drink.type}</td>
                            <td>
                                <img src={drink.image} alt={drink.nameDrink} width="50" />
                            </td>
                            <td>{drink.price}</td>
                            <td>{drink.percentSale}</td>
                            <td>
                                <button onClick={() => handleEdit(drink.id)}>Edit</button>
                            </td>
                            <td>
                                <button onClick={() => handleDelete(drink.id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ListMenu;
