import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const ListMenu = () => {
    const [drinks, setDrinks] = useState([]); // State để lưu danh sách đồ uống
    const [formData, setFormData] = useState(() => {
        // Load formData from localStorage if available
        const savedFormData = localStorage.getItem("formData");
        return savedFormData
            ? JSON.parse(savedFormData)
            : {
                  id: "",
                  nameDrink: "",
                  description: "",
                  type: "",
                  image: null,
                  price: "",
                  percentSale: "",
              };
    });
    const [isEditing, setIsEditing] = useState(false); // State để kiểm tra đang ở chế độ sửa
    const navigate = useNavigate();

    // Save formData to localStorage whenever it changes
    useEffect(() => {
        localStorage.setItem("formData", JSON.stringify(formData));
    }, [formData]);

    // Effect hook để fetch danh sách đồ uống khi component được mount
    useEffect(() => {
        const user = localStorage.getItem("USERLOGIN");
        if (!user) {
            navigate("/admin");
        } else {
            fetchDrinks();
        }
    }, [navigate]);

    // Hàm fetch danh sách đồ uống từ API mock
    const fetchDrinks = async () => {
        try {
            const response = await fetch("https://666fee480900b5f87248b557.mockapi.io/drinks");
            const data = await response.json();
            setDrinks(data);
        } catch (error) {
            console.error("Error fetching drinks:", error);
        }
    };

    // Hàm xử lý khi xoá đồ uống
    const handleDelete = async (id) => {
        try {
            await fetch(`https://666fee480900b5f87248b557.mockapi.io/drinks/${id}`, {
                method: "DELETE",
            });
            setDrinks((prevDrinks) => prevDrinks.filter((drink) => drink.id !== id));
            alert("Xoá Thành Công");
        } catch (error) {
            console.error("Lỗi khi xoá:", error);
        }
    };

    // Hàm xử lý khi click sửa đồ uống
    const handleEdit = async (id) => {
        try {
            const response = await fetch(`https://666fee480900b5f87248b557.mockapi.io/drinks/${id}`);
            const drink = await response.json();
            setFormData({
                id: drink.id,
                nameDrink: drink.nameDrink,
                description: drink.description,
                type: drink.type,
                image: drink.image, // Đảm bảo dữ liệu hình ảnh là hợp lệ
                price: drink.price,
                percentSale: drink.percentSale,
            });
            setIsEditing(true); // Chuyển sang chế độ sửa
        } catch (error) {
            console.error("Lỗi khi sửa:", error);
        }
    };

    // Hàm xử lý khi thay đổi dữ liệu trong form
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    // Hàm xử lý khi thay đổi hình ảnh
    const handleImageChange = (e) => {
        const file = e.target.files[0];
        setFormData({
            ...formData,
            image: file,
        });
    };

    // Hàm xử lý khi cập nhật đồ uống
    const handleUpdate = async () => {
        try {
            const formDataToUpdate = new FormData();
            formDataToUpdate.append("id", formData.id);
            formDataToUpdate.append("nameDrink", formData.nameDrink);
            formDataToUpdate.append("description", formData.description);
            formDataToUpdate.append("type", formData.type);
            formDataToUpdate.append("price", formData.price);
            formDataToUpdate.append("percentSale", formData.percentSale);

            // Kiểm tra xem có chọn ảnh mới không
            if (formData.image && formData.image instanceof File) {
                formDataToUpdate.append("image", formData.image);
            }

            await fetch(`https://666fee480900b5f87248b557.mockapi.io/drinks/${formData.id}`, {
                method: "PUT",
                body: formDataToUpdate,
            });

            // Cập nhật lại danh sách đồ uống sau khi cập nhật thành công
            fetchDrinks();
            alert("Cập nhật Thành Công");
        } catch (error) {
            console.error("Lỗi khi cập nhật:", error);
        }
    };

    // Hàm xử lý khi thêm mới đồ uống
    const handleAdd = async () => {
        try {
            const formDataToAdd = new FormData();
            formDataToAdd.append("nameDrink", formData.nameDrink);
            formDataToAdd.append("description", formData.description);
            formDataToAdd.append("type", formData.type);
            formDataToAdd.append("price", formData.price);
            formDataToAdd.append("percentSale", formData.percentSale);

            if (formData.image) {
                formDataToAdd.append("image", formData.image);
            }

            const response = await fetch("https://666fee480900b5f87248b557.mockapi.io/drinks", {
                method: "POST",
                body: formDataToAdd,
            });
            const newDrink = await response.json();

            // Thêm vào cuối danh sách đồ uống
            setDrinks([...drinks, newDrink]);

            // Reset form sau khi thêm mới thành công
            resetFormData();
            alert("Thêm Thành Công");
        } catch (error) {
            console.error("Lỗi khi thêm mới:", error);
        }
    };

    // Hàm reset form về trạng thái ban đầu
    const resetFormData = () => {
        setFormData({
            id: "",
            nameDrink: "",
            description: "",
            type: "",
            image: null,
            price: "",
            percentSale: "",
        });
        setIsEditing(false); // Chuyển về chế độ thêm mới
    };

    // Effect hook để fetch danh sách đồ uống khi component được mount
    useEffect(() => {
        fetchDrinks();
    }, []);
    return (
        <div className="list-menu">
            <h2>Danh sách Menu</h2>
            <div className="list-menu-input_form">
                <input type="text" name="id" value={formData.id} placeholder="ID" onChange={handleChange} readOnly={!isEditing} />
                <input type="text" name="nameDrink" value={formData.nameDrink} placeholder="Tên Món" onChange={handleChange} />
                <input type="text" name="description" value={formData.description} placeholder="Mô Tả Món" onChange={handleChange} />
                <input type="text" name="type" value={formData.type} placeholder="Loại" onChange={handleChange} />
                <input type="file" name="image" onChange={handleImageChange} />
                <input type="text" name="price" value={formData.price} placeholder="Giá tiền" onChange={handleChange} />
                <input type="text" name="percentSale" value={formData.percentSale} placeholder="Phần trăm Sale" onChange={handleChange} />
                {isEditing ? (
                    <button className="button-edit" onClick={handleUpdate}>
                        Cập nhật
                    </button>
                ) : (
                    <button className="button-delete" onClick={handleAdd}>
                        Thêm
                    </button>
                )}
            </div>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Tên Món</th>
                        <th>Mô Tả Món</th>
                        <th>Loại</th>
                        <th>Hình ảnh</th>
                        <th>Giá tiền</th>
                        <th>Phần trăm Sale</th>
                        <th>Sửa</th>
                        <th>Xoá</th>
                    </tr>
                </thead>
                <tbody>
                    {drinks.map((drink) => (
                        <tr key={drink.id}>
                            <td>{drink.id}</td>
                            <td>{drink.nameDrink}</td>
                            <td>{drink.description}</td>
                            <td>{drink.type}</td>
                            <td>{drink.image && <img src={drink.image} alt={drink.nameDrink} width="50" />}</td>
                            <td>{drink.price}</td>
                            <td>{drink.percentSale}</td>
                            <td>
                                <button className="button-edit" onClick={() => handleEdit(drink.id)}>
                                    Sửa
                                </button>
                            </td>
                            <td>
                                <button className="button-delete" onClick={() => handleDelete(drink.id)}>
                                    Xoá
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ListMenu;
