const MilkTea = () => {
    const [drinks, setDrinks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    //xu li API
    useEffect(() => {
        const fetchDrinks = async () => {
            try {
                const response = await fetch("https://666964302e964a6dfed4f085.mockapi.io/Drinks");
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                const data = await response.json();
                setDrinks(data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchDrinks();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    // Phân loại các mặt hàng theo loại
    const milkTeaDrinks = drinks.filter((drink) => drink.type === "milk tea");
    const freshTeaDrinks = drinks.filter((drink) => drink.type === "fresh tea");
    const noneTeaDrinks = drinks.filter((drink) => drink.type === "none tea");

    //react-slick settings
    var settings = {
        infinite: true,
        speed: 500,
        slidesToScroll: 2,
        slidesToShow: 6,
    };

    return (
        <div className="milkTea">
            <HeadLine headline={"Milk Tea"} />
            <Container>
                <div className="slider-container">
                    <Slider {...settings}>
                        {/* Render danh sách các mặt hàng theo loại */}
                        {milkTeaDrinks.map((drink) => (
                            <div className="milktea-box" key={drink.id}>
                                {/* Nội dung */}
                            </div>
                        ))}
                        {freshTeaDrinks.map((drink) => (
                            <div className="milktea-box" key={drink.id}>
                                {/* Nội dung */}
                            </div>
                        ))}
                        {noneTeaDrinks.map((drink) => (
                            <div className="milktea-box" key={drink.id}>
                                {/* Nội dung */}
                            </div>
                        ))}
                    </Slider>
                </div>
            </Container>
        </div>
    );
};

export default MilkTea;
