import React, { useEffect, useRef, useState } from "react";
import { Container } from "react-bootstrap";
import logo from "../../assets/logo/logo.png";

const Header = () => {
    const divRef = useRef();
    const iconRef = useRef();
    const [menu, setMenu] = useState(false);
    const handleShowMenu = () => {
        setMenu(!menu);
    };
    useEffect(() => {
        const handleClickOutside = (e) => {
            if (e.target === iconRef.current) {
                setMenu(true);
            } else if (!divRef.current.contains(e.target)) {
                setMenu(false);
            }
        };
        document.addEventListener("click", handleClickOutside);
    }, []);

    return (
        <div className={`header  ${menu && "active"}`}>
            <Container className="header-container">
                <div className="header-left">
                    <img className="header-left_logo" src={logo} alt="" />
                    <ul className={`header-left_listmenu  ${menu && "active"}`} ref={divRef}>
                        <div className="header-left_listmenu_close" onClick={() => setMenu(!menu)}>
                            <i className="fa-solid fa-xmark"></i>
                        </div>
                        <li>
                            <a href="/">TRANG CHỦ</a>
                        </li>
                        <li>
                            <a href="/about">VỀ CHÚNG TÔI</a>
                        </li>
                        <li>
                            <a href="">LIÊN HỆ</a>
                        </li>
                        <li>
                            <a href="">NHƯỢNG QUYỀN</a>
                        </li>
                    </ul>
                </div>
                <div className="header-right">
                    <div>
                        <i className="fa-solid fa-cart-shopping"></i>
                        <div className="header-right_quantitycart">
                            <p>1</p>
                        </div>
                    </div>
                </div>
                <div className="header-iconmenu" onClick={handleShowMenu}>
                    <i className="fa-solid fa-bars-staggered" ref={iconRef}></i>
                </div>
            </Container>
        </div>
    );
};

export default Header;
