import React, { useEffect, useRef, useState } from "react";
import logo from "../../assets/logo/logo.png";
import { useNavigate } from "react-router-dom";
import { useCart } from "../../pages/UseContext";

const Header = () => {
    const divRef = useRef(null);
    const iconRef = useRef();
    const [menu, setMenu] = useState(false);
    const navigate = useNavigate();
    const { cart } = useCart();

    const handleShowMenu = () => {
        setMenu(!menu);
    };
    console.log(menu);
    useEffect(() => {
        const handleClickOutside = (e) => {
            if (e.target === iconRef.current) {
                setMenu(true);
            } else if (!divRef.current.contains(e.target)) {
                setMenu(false);
            }
        };
        document.addEventListener("click", handleClickOutside);
    }, []);

    return (
        <div className={`header ${menu && "active"}`}>
            <div className="header-container">
                <div className="header-left">
                    <img className="header-left_logo" src={logo} alt="" />
                    <ul className={`header-left_listmenu ${menu && "active"}`} ref={divRef}>
                        <div className="header-left_listmenu_close" onClick={() => setMenu(!menu)}>
                            <i className="fa-solid fa-xmark"></i>
                        </div>
                        <li>
                            <a href="/">TRANG CHỦ</a>
                        </li>
                        <li>
                            <a href="/about">VỀ CHÚNG TÔI</a>
                        </li>
                        <li>
                            <a href="/contact">LIÊN HỆ</a>
                        </li>
                        <li>
                            <a href="/franchise">NHƯỢNG QUYỀN</a>
                        </li>
                    </ul>
                </div>
                <div className="header-right" onClick={() => navigate("/cart")}>
                    <a href="" className="header-right_quantitycart">
                        <i className="fa-solid fa-cart-shopping"></i> <span>{cart.length}</span>
                    </a>
                </div>
                <div className="header-iconmenu" onClick={handleShowMenu}>
                    <i className="fa-solid fa-bars-staggered" ref={iconRef}></i>
                </div>
            </div>
        </div>
    );
};

export default Header;
