import React, { useState } from "react";
import { Navigate } from "react-router-dom";

const Admin = () => {
    const [formData, setFormData] = useState({
        username: "",
        password: "",
    });
    const [error, setError] = useState("");

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError("");

        try {
            const response = await fetch("https://666fee480900b5f87248b557.mockapi.io/user");
            const users = await response.json();
            const user = users.find((user) => user.username === formData.username);

            if (user && user.password === formData.password) {
                alert("Đăng nhập thành công!");
                Navigate("/admin-dashboard");
            } else {
                setError("Sai tài khoản hoặc mật khẩu");
            }
        } catch (error) {
            console.error("Error fetching users:", error);
            setError("Có lỗi xảy ra, vui lòng thử lại");
        }
    };

    return (
        <div className="login">
            <form className="login-form" onSubmit={handleSubmit}>
                <h2>Đăng Nhập</h2>
                <div className="login-form_input">
                    <label>Tài Khoản</label>
                    <input required type="text" name="username" value={formData.username} onChange={handleChange} />
                </div>
                <div className="login-form_input">
                    <label>Mật khẩu</label>
                    <input required type="password" name="password" value={formData.password} onChange={handleChange} />
                </div>
                {error && <div className="login-form_errorMessage">{error}</div>}
                <button type="submit">Đăng Nhập</button>
            </form>
        </div>
    );
};

export default Admin;
