import React, { useEffect, useState } from "react";

const UseFetch = () => {
    const [drinks, setDrinks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchDrinks = async () => {
            setLoading(true);
            try {
                const response = await fetch("https://666964302e964a6dfed4f085.mockapi.io/Drinks/");
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                const data = await response.json();
                setDrinks(data);
            } catch (error) {
                setError(error);
            } finally {
                setLoading(false);
            }
        };

        fetchDrinks();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Xin hãy load lại trang</div>; // Hiển thị message của error
    }

    return drinks;
};

export default UseFetch;
