import React, { useState } from "react";
import { Col, Container, Row } from "react-bootstrap";

const Map = () => {
    const arrayLocation = [
        {
            id: 1,
            name: "Thủ Đức",
            address: "380 Bùi Hữu Nghĩa, Phường 2, Bình Thạnh (đối diện chợ Bà Chiểu)",
            img: "https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3918.5115758814186!2d106.76372787464591!3d10.84864055785833!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x317527002adbc953%3A0x655feb004c8d22c8!2sShaman%20Tea!5e0!3m2!1sen!2s!4v1718809159837!5m2!1sen!2s",
            link: "https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3918.5115758814186!2d106.76372787464591!3d10.84864055785833!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x317527002adbc953%3A0x655feb004c8d22c8!2sShaman%20Tea!5e0!3m2!1sen!2s!4v1718809159837!5m2!1sen!2s",
        },
        {
            id: 2,
            name: "Go Vap",
            address: "50 Dương Quảng Hàm, Phường 2, Gò Vấp",
            img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQAt7Mkf_0UsT4DvoeT3QT_o2ywZUqU_RfMNQ&s",
            link: "https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d52184.15720579712!2d106.6732108918185!3d10.799988094807926!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x31752949eb3e795f%3A0xa435dd1685ea2fbc!2sHoang%20Van%20Thu%20Park!5e0!3m2!1sen!2s!4v1718165247572!5m2!1sen!2s",
        },
    ];
    const [location, setLocation] = useState(arrayLocation[0].link);

    return (
        <div className="map">
            <Container>
                <Row>
                    <Col md={7}>
                        <div className="map-location">
                            <iframe src={location} width="100%" height="450" allowfullscreen="" loading="lazy" referrerpolicy="no-referrer-when-downgrade"></iframe>
                        </div>
                    </Col>
                    <Col md={5}>
                        <div className="map-system">
                            <h2>Branch System</h2>
                            <div className="map-system-scroll">
                                <div className="map-system-box">
                                    {arrayLocation.map((item) => (
                                        <div className="map-card" onClick={() => setLocation(item.link)}>
                                            <div className="map-card-branch">
                                                <h3>
                                                    <i className="fa-solid fa-map-pin"></i>
                                                    {item.name}
                                                </h3>
                                                <h4>{item.address}</h4>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            </div>
                        </div>
                    </Col>
                </Row>
                ;
            </Container>
        </div>
    );
};

export default Map;
