import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { Container } from "react-bootstrap";
import React, { useEffect, useState } from "react";
import Slider from "react-slick";
import HeadLine from "../../../component/Headline/Headline";

const MilkTea = () => {
    const [drinks, setDrinks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchDrinks = async () => {
            try {
                const response = await fetch("https://666964302e964a6dfed4f085.mockapi.io/Drinks");
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                const data = await response.json();
                setDrinks(data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchDrinks();
    }, []);

    var settings = {
        infinite: false,
        speed: 500,
        slidesToScroll: 2,
        slidesToShow: 6,
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="milkTea">
            <HeadLine headline={"Milk Tea"} />
            <Container>
                <div className="slider-container">
                    <Slider {...settings}>
                        {drinks.map((drink) => (
                            <div className="milktea-box" key={drink.id}>
                                <div>
                                    <img src={drink.image} alt={drink.name} />
                                    <p>{drink.description}</p>
                                </div>
                                <div>
                                    <h3>{drink.nameDrink}</h3>
                                    <h4>
                                        ${drink.price} <span>${drink.priceSale}</span>
                                    </h4>
                                </div>
                            </div>
                        ))}
                    </Slider>
                </div>
            </Container>
        </div>
    );
};

export default MilkTea;
