// MilkTea.js

import React, { useEffect, useState } from "react";
import { Container, Row, Col } from "react-bootstrap"; // Import các component grid từ react-bootstrap
import HeadLine from "../../../component/Headline/Headline";
import Button from "../../../component/Button/Button";

const MilkTea = () => {
    const [drinks, setDrinks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [currentIndex, setCurrentIndex] = useState(0);

    // Xử lý API
    useEffect(() => {
        const fetchDrinks = async () => {
            try {
                const response = await fetch("https://666964302e964a6dfed4f085.mockapi.io/Drinks");
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                const data = await response.json();
                setDrinks(data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchDrinks();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    // Hàm để tính giá sau khi giảm giá và định dạng số tiền
    const formatCurrency = (amount) => {
        return new Intl.NumberFormat("vi-VN", { style: "currency", currency: "VND" }).format(amount).replace(/\B(?=(\d{3})+(?!\d))/g, ".");
    };

    // Điều hướng đến sản phẩm trước
    const prevDrink = () => {
        setCurrentIndex((prev) => (prev === 0 ? drinks.length - 1 : prev - 1));
    };

    // Điều hướng đến sản phẩm tiếp theo
    const nextDrink = () => {
        setCurrentIndex((prev) => (prev + 1) % drinks.length);
    };

    return (
        <Container>
            <div className="milkTea">
                {drinks.map((drink, index) => (
                    <div key={drink.id} style={{ display: index === currentIndex ? "block" : "none" }}>
                        <HeadLine headline={`${drink.type}`} />
                        <Row className="milkTea-container">
                            <Col xs={12} sm={6} md={4} lg={3}>
                                <div className="milktea-box">
                                    <div className="milkTea-box_image">
                                        <img src={drink.image} alt={drink.name} />
                                        {drink.percentSale !== "0" && <p>{drink.percentSale}%</p>}
                                    </div>
                                    <div className="milkTea-box_title">
                                        <h2>{drink.nameDrink}</h2>
                                        <div className="milkTea-box_price">
                                            <h3 className={drink.percentSale !== "0" ? "sale" : ""}>{formatCurrency(drink.price)}</h3>
                                            {drink.percentSale !== "0" && <h4>{formatCurrency(drink.price - (drink.price * drink.percentSale) / 100)}</h4>}
                                        </div>
                                    </div>
                                    <Button secondary="true" text="Đặt hàng ngay" link=""></Button>
                                </div>
                            </Col>
                        </Row>
                    </div>
                ))}
                <div className="arrow-prev" onClick={prevDrink}>
                    Prev
                </div>
                <div className="arrow-next" onClick={nextDrink}>
                    Next
                </div>
            </div>
        </Container>
    );
};

export default MilkTea;
