import { createContext, useContext, useState } from "react";
import { toast } from "react-toastify";
const UseContext = createContext();
const CartProvider = ({ children }) => {
    const [cart, setCart] = useState(localStorage.getItem("CARTPRODUCT") ? JSON.parse(localStorage.getItem("CARTPRODUCT")) : []);
    const addToCart = (product) => {
        const newCart = [...cart];
        const findIdProduct = newCart.findIndex((item) => item.id === product.id);
        if (findIdProduct >= 0) {
            newCart[findIdProduct].quantity = Number(newCart[findIdProduct].quantity) + product.quantity;
        } else {
            newCart.push(product);
        }
        localStorage.setItem("CARTPRODUCT", JSON.stringify(newCart));
        setCart(newCart);
        toast.success("Success Notification !", {
            position: "top-center",
        });
    };
    const deleteCart = (id) => {
        const newCart = [...cart];
        const findId = cart.findIndex((item) => item.id === id);
        newCart.splice(findId, 1);
        setCart(newCart);
        localStorage.setItem("CARTPRODUCT", JSON.stringify(newCart));
    };

    return <UseContext.Provider value={{ addToCart, deleteCart, cart }}>{children}</UseContext.Provider>;
};
const useCart = () => {
    return useContext(UseContext);
};
export { CartProvider, useCart };
