import React, { useEffect, useRef, useState } from "react";
import { Container } from "react-bootstrap";
import logo from "../../assets/logo/logo.png";

const Header = () => {
    const divRef = useRef(null);
    const iconRef = useRef(null);
    const [menu, setMenu] = useState(false);

    const handleShowMenu = () => {
        setMenu(!menu);
    };

    useEffect(() => {
        const handleClickOutside = (e) => {
            // Đóng menu nếu click vào icon menu
            if (iconRef.current && e.target === iconRef.current) {
                setMenu(!menu);
            } else if (divRef.current && !divRef.current.contains(e.target)) {
                // Đóng menu nếu click bên ngoài menu
                setMenu(false);
            }
        };

        // Thêm sự kiện click toàn cục để xử lý đóng menu
        document.addEventListener("click", handleClickOutside);

        // Cleanup function để loại bỏ sự kiện khi component unmount
        return () => {
            document.removeEventListener("click", handleClickOutside);
        };
    }, [menu]); // Thêm menu vào dependency để useEffect được gọi lại khi menu thay đổi

    return (
        <div className={`header ${menu ? "active" : ""}`}>
            <Container className="header-container">
                <div className="header-left">
                    <img className="header-left_logo" src={logo} alt="" />
                    <ul className={`header-left_listmenu ${menu ? "active" : ""}`} ref={divRef}>
                        <div className="header-left_listmenu_close" onClick={() => setMenu(false)}>
                            <i className="fa-solid fa-xmark"></i>
                        </div>
                        <li>
                            <a href="/">TRANG CHỦ</a>
                        </li>
                        <li>
                            <a href="/about">VỀ CHÚNG TÔI</a>
                        </li>
                        <li>
                            <a href="">LIÊN HỆ</a>
                        </li>
                        <li>
                            <a href="">NHƯỢNG QUYỀN</a>
                        </li>
                    </ul>
                </div>
                <div className="header-right">
                    <a href="" className="header-right_quantitycart">
                        <i className="fa-solid fa-cart-shopping"></i> <span>2</span>
                    </a>
                </div>
                <div className="header-iconmenu" onClick={handleShowMenu}>
                    <i className="fa-solid fa-bars-staggered" ref={iconRef}></i>
                </div>
            </Container>
        </div>
    );
};

export default Header;
