import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import React, { useEffect, useState } from "react";
import { Container } from "react-bootstrap";
import Slider from "react-slick";
import HeadLine from "../../../component/Headline/Headline";
import Button from "../../../component/Button/Button";

const MilkTea = () => {
    const [drinks, setDrinks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Xử lý API
    useEffect(() => {
        const fetchDrinks = async () => {
            try {
                const response = await fetch("https://666964302e964a6dfed4f085.mockapi.io/Drinks");
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                const data = await response.json();
                setDrinks(data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchDrinks();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    // Hàm để tính giá sau khi giảm giá và định dạng số tiền
    const formatCurrency = (amount) => {
        return new Intl.NumberFormat("vi-VN", { style: "currency", currency: "VND" }).format(amount).replace(/\B(?=(\d{3})+(?!\d))/g, ".");
    };

    // react-slick settings

    var settings = {
        dots: false,
        infinite: false,
        speed: 500,
        slidesToShow: 5,
        slidesToScroll: 5,
        initialSlide: 0,
        prevArrow: <CustomPrevArrow />,
        nextArrow: <CustomNextArrow />,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 4,
                    slidesToScroll: 4,
                    infinite: true,
                    dots: true,
                },
            },
            {
                breakpoint: 767,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    initialSlide: 3,
                },
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2,
                },
            },
        ],
    };

    // Lọc ra các loại sản phẩm duy nhất
    const uniqueTypes = [...new Set(drinks.map((drink) => drink.type))];

    return (
        <Container>
            <div className="milkTea-container">
                {uniqueTypes.map((type) => (
                    <div key={type}>
                        <HeadLine headline={type} />
                        <Slider {...settings}>
                            {drinks
                                .filter((drink) => drink.type === type)
                                .map((drink) => (
                                    <div className="milktea-box" key={drink.id}>
                                        <div className="milkTea-box_image">
                                            <img src={drink.image} alt={drink.name} />
                                            {drink.percentSale !== "0" && <p>{drink.percentSale}%</p>}
                                        </div>
                                        <div className="milkTea-box_title">
                                            <h2>{drink.nameDrink}</h2>
                                            <div className="milkTea-box_price">
                                                <h3 className={drink.percentSale !== "0" ? "sale" : ""}>{formatCurrency(drink.price)}</h3>
                                                {drink.percentSale !== "0" && <h4>{formatCurrency(drink.price - (drink.price * drink.percentSale) / 100)}</h4>}
                                            </div>
                                        </div>
                                        <Button secondary text="Đặt hàng ngay" link="" />
                                    </div>
                                ))}
                        </Slider>
                    </div>
                ))}
            </div>
        </Container>
    );
};

export default MilkTea;
