import React, { useEffect, useState } from "react";
import { Container, Col, Row } from "react-bootstrap";
import { useParams, useNavigate } from "react-router-dom";
import Button from "../../component/Button/Button";
import image from "../../assets/product1/product1.png";
import HeadLine from "../../component/Headline/Headline";
import Slider from "react-slick";

const OrderProduct = () => {
    const { drinkId } = useParams();
    const [drinks, setDrinks] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const navigate = useNavigate();

    // Quantity handling
    const [quantity, setQuantity] = useState(1);

    const handleChooseQuantity = (type) => {
        if (type === "plus") {
            setQuantity(quantity + 1);
        } else if (type === "minus" && quantity > 1) {
            setQuantity(quantity - 1);
        }
    };

    // Fetch drinks data from API
    useEffect(() => {
        const fetchDrinks = async () => {
            try {
                const response = await fetch(`https://666964302e964a6dfed4f085.mockapi.io/Drinks/${drinkId}`);
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                const data = await response.json();
                setDrinks(data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchDrinks();
    }, [drinkId]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Xin hãy load lại trang</div>;
    }

    // Format currency function
    const formatCurrency = (amount) => {
        return new Intl.NumberFormat("vi-VN", { style: "currency", currency: "VND" }).format(amount).replace(/\B(?=(\d{3})+(?!\d))/g, ".");
    };

    // Handle order button click
    const handleOrderClick = () => {
        navigate(`/orderproduct/${drinkId}`);
    };

    // Prepare topping drinks
    let toppingDrinks = [];
    if (drinks && drinks.type === "Topping") {
        toppingDrinks = [drinks]; // Place the current drink in an array
    }

    // react-slick settings
    const settings = {
        dots: false,
        infinite: false,
        speed: 500,
        slidesToShow: 5,
        slidesToScroll: 5,
        initialSlide: 0,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 4,
                    slidesToScroll: 4,
                    infinite: true,
                    dots: true,
                },
            },
            {
                breakpoint: 767,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    initialSlide: 3,
                },
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2,
                },
            },
        ],
    };

    return (
        <div className="orderproduct">
            <Container>
                <div className="orderproduct-back" onClick={() => navigate("/")}>
                    <i className="fa-solid fa-chevron-left"></i>
                    <p>Trang chủ</p>
                </div>
                <Row>
                    <Col md={12} lg={6}>
                        <div className="orderproduct-images">
                            <img src={drinks.image} alt="" />
                            {drinks.percentSale !== "0" && <p>{drinks.percentSale}%</p>}
                        </div>
                    </Col>
                    <Col md={12} lg={6}>
                        <div className="orderproduct-choose">
                            <h2>{drinks.nameDrink}</h2>
                            <div className="orderproduct-price">
                                <h4>
                                    {drinks.percentSale !== "0" && (
                                        <>
                                            <span className="priceLineThrough">{formatCurrency(drinks.price)}</span>
                                            <span className="priceSale">{formatCurrency(drinks.price - (drinks.price * drinks.percentSale) / 100)}</span>
                                        </>
                                    )}
                                    {drinks.percentSale === "0" && <span className="priceNoSale">{formatCurrency(drinks.price)}</span>}
                                </h4>
                                <p>{drinks.description}</p>
                            </div>
                            <div className="orderproduct-choose_quantity">
                                <i className="fa-solid fa-minus" onClick={() => handleChooseQuantity("minus")}></i>
                                <h5>{quantity}</h5>
                                <i className="fa-solid fa-plus" onClick={() => handleChooseQuantity("plus")}></i>
                            </div>
                            <div className="orderproduct-choose_order">
                                <Button secondary text="Đặt hàng" onClick={handleOrderClick} />
                                <Button text="Mua Ngay" link="" />
                            </div>
                        </div>
                    </Col>
                </Row>
                <div className="milkTea-container">
                    <div className="milkTea-parent">
                        <HeadLine headline="Topping" />
                        <Slider {...settings}>
                            {toppingDrinks.map((drink) => (
                                <div className="milktea-box" key={drink.id}>
                                    <div className="milkTea-box_image">
                                        <img src={drink.image} alt={drink.nameDrink} />
                                        {drink.percentSale !== "0" && <p>{drink.percentSale}%</p>}
                                    </div>
                                    <div className="milkTea-box_title">
                                        <h2>{drink.nameDrink}</h2>
                                    </div>
                                    <Button text="Đặt hàng" link="" />
                                </div>
                            ))}
                        </Slider>
                    </div>
                </div>
            </Container>
        </div>
    );
};

export default OrderProduct;
