import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const ListMenu = () => {
    const [drinks, setDrinks] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const user = localStorage.getItem("USERLOGIN");
        if (!user) {
            navigate("/admin");
        } else {
            fetchDrinks();
        }
    }, [navigate]);

    const fetchDrinks = async () => {
        try {
            const response = await fetch("https://666fee480900b5f87248b557.mockapi.io/drinks");
            const data = await response.json();
            setDrinks(data);
        } catch (error) {
            console.error("Error fetching drinks:", error);
        }
    };

    const handleDelete = (id) => {};

    const handleEdit = (id) => {};

    return (
        <div className="list-menu">
            <h2>List of Drinks</h2>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Tên Món</th>
                        <th>Mô Tả Món</th>
                        <th>Loại</th>
                        <th>Hình ảnh</th>
                        <th>Giá tiền</th>
                        <th>Phần trăm Sale</th>
                        <th>Sửa</th>
                        <th>Xoá</th>
                    </tr>
                </thead>
                <tbody>
                    {drinks.map((drink) => (
                        <tr key={drink.id}>
                            <td>{drink.id}</td>
                            <td>{drink.nameDrink}</td>
                            <td>{drink.description}</td>
                            <td>{drink.type}</td>
                            <td>
                                <img src={drink.image} alt={drink.nameDrink} width="50" />
                            </td>
                            <td>{drink.price}</td>
                            <td>{drink.percentSale}</td>
                            <td>
                                <button onClick={() => handleEdit(drink.id)}>Edit</button>
                            </td>
                            <td>
                                <button onClick={() => handleDelete(drink.id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ListMenu;
