import React, { useEffect, useState } from "react";
import { Col, Container, Row } from "react-bootstrap";
import Button from "../../component/Button/Button";
import image from "../../assets/product1/product1.png";
import HeadLine from "../../component/Headline/Headline";
import Slider from "react-slick";

const OrderProduct = () => {
    const [quantity, setQuantity] = useState(1);
    const [drinks, setDrinks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    //Quantity
    const handleChooseQuantity = (type) => {
        if (type === "plus") {
            setQuantity(quantity + 1);
        } else if (type === "minus" && quantity > 1) {
            setQuantity(quantity - 1);
        }
    };

    // Xử lý API
    useEffect(() => {
        const fetchDrinks = async () => {
            try {
                const response = await fetch("https://666964302e964a6dfed4f085.mockapi.io/Drinks");
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                const data = await response.json();
                setDrinks(data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchDrinks();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Xin hãy load lại trang {error}</div>;
    }

    // Hàm để tính giá sau khi giảm giá và định dạng số tiền
    const formatCurrency = (amount) => {
        return new Intl.NumberFormat("vi-VN", { style: "currency", currency: "VND" }).format(amount).replace(/\B(?=(\d{3})+(?!\d))/g, ".");
    };
    // react-slick settings
    var settings = {
        dots: false,
        infinite: false,
        speed: 500,
        slidesToShow: 5,
        slidesToScroll: 5,
        initialSlide: 0,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 4,
                    slidesToScroll: 4,
                    infinite: true,
                    dots: true,
                },
            },
            {
                breakpoint: 767,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    initialSlide: 3,
                },
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2,
                },
            },
        ],
    };

    // Lọc ra các loại sản phẩm duy nhất
    const uniqueTypes = [...new Set(drinks.map((drink) => drink.type))];

    return (
        <div className="orderproduct">
            <Container>
                <Row>
                    <Col md={12} lg={6}>
                        <div className="orderproduct-images">
                            <img src={image} alt="" />
                            <p>10%</p>
                        </div>
                    </Col>
                    <Col md={12} lg={6}>
                        <div className="orderproduct-choose">
                            <h2>Shaman Tea</h2>
                            <div className="orderproduct-price">
                                <h4>
                                    <span>30.000 </span>23.000 vnd
                                </h4>
                            </div>
                            <p>
                                Lorem, ipsum dolor sit amet consectetur adipisicing elit. Error, maiores! Explicabo eaque fugit ullam aut natus repellendus, at, facilis pariatur inventore architecto
                                consequatur commodi dolorem voluptate quam incidunt veniam ea?
                            </p>
                            <div className="orderproduct-choose_quantity">
                                <i className="fa-solid fa-minus" onClick={() => handleChooseQuantity("minus")}></i>
                                <h5>{quantity}</h5>
                                <i className="fa-solid fa-plus" onClick={() => handleChooseQuantity("plus")}></i>
                            </div>
                            <div className="orderproduct-choose_order">
                                <Button secondary text="Đặt hàng" link="" />
                                <Button text="Mua Ngay" link="" />
                            </div>
                        </div>
                    </Col>
                </Row>

                <div className="milkTea-container">
                    {uniqueTypes.map((type) => (
                        <div className="milkTea-parent" key={type}>
                            <HeadLine headline={type} />
                            <Slider {...settings}>
                                {drinks
                                    .filter((drink) => drink.type === type)
                                    .map((drink) => (
                                        <div className="milktea-box" key={drink.id}>
                                            <div className="milkTea-box_image">
                                                <img src={drink.image} alt={drink.name} />
                                                {drink.percentSale !== "0" && <p>{drink.percentSale}%</p>}
                                            </div>
                                            <div className="milkTea-box_title">
                                                <h2>{drink.nameDrink}</h2>
                                            </div>
                                            <Button text="Đặt hàng" link="" />
                                        </div>
                                    ))}
                            </Slider>
                        </div>
                    ))}
                </div>
            </Container>
        </div>
    );
};

export default OrderProduct;
