import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const ListMenu = () => {
    const navigate = useNavigate();
    const [drinks, setDrinks] = useState([]);
    const [formData, setFormData] = useState({
        nameDrink: "",
        description: "",
        type: "",
        image: null,
        price: "",
        percentSale: "",
    });
    const [formErrors, setFormErrors] = useState({
        nameDrink: "",
        description: "",
        type: "",
        price: "",
        percentSale: "",
    });

    useEffect(() => {
        const user = localStorage.getItem("USERLOGIN");
        if (!user) {
            navigate("/admin");
        } else {
            fetchDrinks();
        }
    }, []);

    const fetchDrinks = async () => {
        try {
            const response = await fetch("https://666fee480900b5f87248b557.mockapi.io/drinks");
            const data = await response.json();
            setDrinks(data);
        } catch (error) {
            console.error("Error fetching drinks:", error);
        }
    };

    const handleDelete = async (id) => {
        try {
            await fetch(`https://666fee480900b5f87248b557.mockapi.io/drinks/${id}`, {
                method: "DELETE",
            });
            setDrinks((prevDrinks) => prevDrinks.filter((drink) => drink.id !== id));
            alert("Xoá Thành Công");
        } catch (error) {
            console.error("Lỗi khi xoá:", error);
        }
    };

    const handleAdd = async () => {
        try {
            const formDataToAdd = new FormData();
            formDataToAdd.append("nameDrink", formData.nameDrink);
            formDataToAdd.append("description", formData.description);
            formDataToAdd.append("type", formData.type);
            formDataToAdd.append("price", formData.price);
            formDataToAdd.append("percentSale", formData.percentSale);
            formDataToAdd.append("image", formData.image);

            const response = await fetch("https://666fee480900b5f87248b557.mockapi.io/drinks", {
                method: "POST",
                body: formDataToAdd,
            });

            const newDrinkData = await response.json();

            setDrinks((prevDrinks) => [...prevDrinks, newDrinkData]);
            resetFormData();
            alert("Thêm Thành Công");
        } catch (error) {
            console.error("Lỗi khi thêm mới:", error);
            alert("Thêm mới không thành công. Vui lòng thử lại sau.");
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });

        // Clear the error message when user starts typing again
        setFormErrors({
            ...formErrors,
            [name]: "",
        });
    };

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        setFormData({
            ...formData,
            image: file,
        });
    };

    const resetFormData = () => {
        setFormData({
            nameDrink: "",
            description: "",
            type: "",
            image: null,
            price: "",
            percentSale: "",
        });

        setFormErrors({
            nameDrink: "",
            description: "",
            type: "",
            price: "",
            percentSale: "",
        });
    };

    const isFormValid = () => {
        let valid = true;
        const errors = {
            nameDrink: "",
            description: "",
            type: "",
            price: "",
            percentSale: "",
        };

        if (!formData.nameDrink) {
            errors.nameDrink = "Vui lòng nhập Tên Món";
            valid = false;
        }

        if (!formData.description) {
            errors.description = "Vui lòng nhập Mô Tả Món";
            valid = false;
        }

        if (!formData.type) {
            errors.type = "Vui lòng nhập Loại";
            valid = false;
        }

        if (!formData.price) {
            errors.price = "Vui lòng nhập Giá tiền";
            valid = false;
        }

        if (!formData.percentSale) {
            errors.percentSale = "Vui lòng nhập Phần trăm Sale";
            valid = false;
        }

        setFormErrors(errors);
        return valid;
    };

    return (
        <div className="list-menu">
            <h2>Danh sách Menu</h2>
            <div className="list-menu-input_form">
                <input type="text" name="nameDrink" value={formData.nameDrink} placeholder="Tên Món" onChange={handleChange} />
                {formErrors.nameDrink && <span className="list-menu-input_form_error">{formErrors.nameDrink}</span>}
                <input type="text" name="description" value={formData.description} placeholder="Mô Tả Món" onChange={handleChange} />
                {formErrors.description && <span className="list-menu-input_form_error">{formErrors.description}</span>}
                <input type="text" name="type" value={formData.type} placeholder="Loại" onChange={handleChange} />
                {formErrors.type && <span className="list-menu-input_form_error">{formErrors.type}</span>}
                <input type="file" name="image" onChange={handleImageChange} />
                <input type="text" name="price" value={formData.price} placeholder="Giá tiền" onChange={handleChange} />
                {formErrors.price && <span className="list-menu-input_form_error">{formErrors.price}</span>}
                <input type="text" name="percentSale" value={formData.percentSale} placeholder="Phần trăm Sale" onChange={handleChange} />
                {formErrors.percentSale && <span className="list-menu-input_form_error">{formErrors.percentSale}</span>}
            </div>
            <button className="button-delete" onClick={handleAdd}>
                Thêm
            </button>
            <table>
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Tên Món</th>
                        <th>Mô Tả Món</th>
                        <th>Loại</th>
                        <th>Hình ảnh</th>
                        <th>Giá tiền</th>
                        <th>Phần trăm Sale</th>
                        <th>Xoá</th>
                    </tr>
                </thead>
                <tbody>
                    {drinks.map((drink) => (
                        <tr key={drink.id}>
                            <td>{drink.id}</td>
                            <td>{drink.nameDrink}</td>
                            <td>{drink.description}</td>
                            <td>{drink.type}</td>
                            <td>{drink.image && <img src={drink.image} alt={drink.nameDrink} width="50" />}</td>
                            <td>{drink.price}</td>
                            <td>{drink.percentSale}</td>
                            <td>
                                <button className="button-delete" onClick={() => handleDelete(drink.id)}>
                                    Xoá
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ListMenu;
