import React, { useEffect, useState } from "react";
import { Container, Col, Row } from "react-bootstrap";
import { useParams, useNavigate } from "react-router-dom";
import Button from "../../component/Button/Button";

const OrderProduct = () => {
    const { drinkId } = useParams();
    const [drink, setDrink] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [quantity, setQuantity] = useState(1);

    const navigate = useNavigate();

    // Fetch drink data from API
    useEffect(() => {
        const fetchDrink = async () => {
            try {
                const response = await fetch(`https://666fee480900b5f87248b557.mockapi.io/drinks/${drinkId}`);
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                const data = await response.json();
                setDrink(data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchDrink();
    }, [drinkId]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error || !drink) {
        return <div>Xin hãy load lại trang</div>;
    }

    // Handle quantity changes
    const handleQuantity = (type) => {
        if (type === "plus") {
            setQuantity(quantity + 1);
        } else {
            if (quantity > 1) {
                setQuantity(quantity - 1);
            }
        }
    };

    // Format currency function
    const formatCurrency = (amount) => {
        return new Intl.NumberFormat("vi-VN", { style: "currency", currency: "VND" }).format(amount).replace(/\B(?=(\d{3})+(?!\d))/g, ".");
    };

    // Create the product object with dynamic data

    return (
        <div className="orderproduct">
            <Container>
                <div className="orderproduct-back" onClick={() => navigate("/")}>
                    <i className="fa-solid fa-chevron-left"></i>
                    <p>Trang chủ</p>
                </div>
                <Row>
                    <Col md={12} lg={6}>
                        <div className="orderproduct-images">
                            <img src={drink.image} alt={drink.name} />
                            {drink.percentSale !== "0" && <p>{drink.percentSale}%</p>}
                        </div>
                    </Col>
                    <Col md={12} lg={6}>
                        <div className="orderproduct-choose">
                            <h2>{drink.nameDrink}</h2>
                            <div className="orderproduct-price">
                                <h4>
                                    {drink.percentSale !== "0" ? (
                                        <>
                                            <span className="priceLineThrough">{formatCurrency(drink.price)}</span>
                                            <span className="priceSale">{formatCurrency(drink.price - (drink.price * drink.percentSale) / 100)}</span>
                                        </>
                                    ) : (
                                        <span className="priceNoSale">{formatCurrency(drink.price)}</span>
                                    )}
                                </h4>
                                <p>{drink.description}</p>
                            </div>
                            <div className="orderproduct-choose_quantity">
                                <i className="fa-solid fa-minus" onClick={() => handleQuantity("minus")}></i>
                                <h5>{quantity}</h5>
                                <i className="fa-solid fa-plus" onClick={() => handleQuantity("plus")}></i>
                            </div>
                            <a
                                rel="noopener noreferrer"
                                href="https://order.ipos.vn/menu?pos_parent=BRAND-WBHA&pos_id=96862&latest=true"
                                target="_blank"
                                className="orderproduct-choose_order"
                                style="border-radius:5px;"
                            >
                                <i class="icon-shopping-cart" aria-hidden="true"></i> <span>ĐẶT HÀNG</span>
                            </a>
                            <a href="https://order.ipos.vn/menu?pos_parent=BRAND-WBHA&pos_id=96862&latest=true" className="orderproduct-choose_order">
                                <Button secondary text="Đặt hàng" />
                            </a>
                        </div>
                    </Col>
                </Row>
            </Container>
        </div>
    );
};

export default OrderProduct;
